mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*,c.name as category_name from dish d left outer join category c on d.category_id = c.id

    and d.name like concat('%',#{name},'%')
    and d.category_id = #{categoryId}
    and d.status = #{status}

order by d.create_time desc

select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
