mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image},
where id = #{id}

mapper.select:
select s.* ,c.name categoryName from setmeal s left join category c on s.category_id = c.id

    and s.name = concat('%',#{name},'%')
    and s.category_id = #{categoryId}
    and s.status = #{status}

order by s.create_time desc
